import jakarta.persistence.*;
import lombok.AllArgsConstructor;
import lombok.Getter;
import lombok.NoArgsConstructor;
import lombok.Setter;
import java.util.Set;

@Entity
@Table(name = "fdh")
@Getter
@Setter
@NoArgsConstructor
@AllArgsConstructor
public class Fdh {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    @Column(name = "fdh_id")
    private Integer id;

    private String name;
    private String location;
    private String region;
    private int maxPorts;

    // Many FDHs belong to one Headend
    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "headend_id") // This matches your SQL
    private Headend headend;

    // One FDH has many Splitters
    @OneToMany(mappedBy = "fdh", cascade = CascadeType.ALL, fetch = FetchType.LAZY)
    private Set<Splitter> splitters;
}

