@Entity
@Table(name = "splitters") // I'll pluralize "Splitter" to follow convention
@Getter
@Setter
@NoArgsConstructor
@AllArgsConstructor
public class Splitter {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    @Column(name = "splitter_id")
    private Integer id;

    private String model;
    private int portCapacity;
    private int usedPorts;
    private String location;

    // Many Splitters belong to one FDH
    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "fdh_id") // This matches your SQL
    private Fdh fdh;

    // One Splitter serves many Customers
    @OneToMany(mappedBy = "splitter", fetch = FetchType.LAZY)
    private Set<Customer> customers;

    // One Splitter is the source for many Fiber Drop Lines
    @OneToMany(mappedBy = "splitter", fetch = FetchType.LAZY)
    private Set<FiberDropLine> fiberDropLines;
}

