import com.example.inventory.model.enums.ConnectionType;
import com.example.inventory.model.enums.CustomerStatus;
import jakarta.persistence.*;
import lombok.AllArgsConstructor;
import lombok.Getter;
import lombok.NoArgsConstructor;
import lombok.Setter;
import org.hibernate.annotations.CreationTimestamp;
import java.time.LocalDateTime;
import java.util.Set;

@Entity
@Table(name = "customers") // Pluralized
@Getter
@Setter
@NoArgsConstructor
@AllArgsConstructor
public class Customer {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    @Column(name = "customer_id")
    private Integer id;

    private String name;

    @Column(columnDefinition = "TEXT")
    private String address;

    private String neighborhood;
    private String plan;

    @Enumerated(EnumType.STRING)
    private ConnectionType connectionType;

    @Enumerated(EnumType.STRING)
    private CustomerStatus status;

    @Column(name = "assigned_port")
    private int assignedPort;

    @CreationTimestamp // Automatically sets the time when created
    @Column(updatable = false)
    private LocalDateTime createdAt;

    // Many Customers are connected to one Splitter
    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "splitter_id") // This matches your SQL
    private Splitter splitter;

    // One Customer has many AssignedAssets (the join table)
    @OneToMany(mappedBy = "customer", cascade = CascadeType.ALL, orphanRemoval = true)
    private Set<AssignedAssets> assignedAssets;

    // One Customer has one Fiber Drop Line
    @OneToOne(mappedBy = "customer", cascade = CascadeType.ALL, fetch = FetchType.LAZY)
    private FiberDropLine fiberDropLine;

    // One Customer can have many Deployment Tasks
    @OneToMany(mappedBy = "customer", fetch = FetchType.LAZY)
    private Set<DeploymentTask> deploymentTasks;
}

