import com.example.inventory.dto.CustomerDTO;
import com.example.inventory.entity.Customer;
import com.example.inventory.entity.Splitter;
import com.example.inventory.repository.CustomerRepository;
import com.example.inventory.repository.SplitterRepository; // We'll need this soon
import jakarta.persistence.EntityNotFoundException;
import lombok.RequiredArgsConstructor;
import org.springframework.stereotype.Service;

import java.util.List;
import java.util.stream.Collectors;

/**
 * Implementation of the CustomerService interface.
 * This class contains the core business logic for customer operations.
 */
@Service // Marks this class as a Spring service bean
@RequiredArgsConstructor // Lombok annotation to create a constructor for all final fields
public class CustomerServiceImpl implements CustomerService {

    // Dependency Injection: Spring will automatically provide an instance of our repository.
    private final CustomerRepository customerRepository;

    // We inject the SplitterRepository to link a customer to a splitter.
    // You will need to create this repository interface similar to CustomerRepository.
    private final SplitterRepository splitterRepository;

    @Override
    public CustomerDTO createCustomer(CustomerDTO customerDTO) {
        // 1. Convert the incoming DTO to a Customer entity
        Customer customer = toEntity(customerDTO);

        // 2. Save the new Customer entity using the repository
        Customer savedCustomer = customerRepository.save(customer);

        // 3. Convert the saved entity back to a DTO to return to the caller
        return toDTO(savedCustomer);
    }

    @Override
    public CustomerDTO getCustomerById(Integer id) {
        // Find the customer by ID or throw an exception if it doesn't exist
        Customer customer = customerRepository.findById(id)
                .orElseThrow(() -> new EntityNotFoundException("Customer not found with id: " + id));
        return toDTO(customer);
    }

    @Override
    public List<CustomerDTO> getAllCustomers() {
        // Fetch all customers from the database
        List<Customer> customers = customerRepository.findAll();

        // Convert the list of entities to a list of DTOs and return it
        return customers.stream()
                .map(this::toDTO)
                .collect(Collectors.toList());
    }

    // --- Private Helper Methods (Mappers) ---

    /**
     * Converts a Customer entity to a CustomerDTO.
     */
    private CustomerDTO toDTO(Customer customer) {
        return new CustomerDTO(
                customer.getId(),
                customer.getName(),
                customer.getAddress(),
                customer.getNeighborhood(),
                customer.getPlan(),
                customer.getConnectionType(),
                customer.getStatus(),
                customer.getAssignedPort(),
                customer.getCreatedAt(),
                // Safely get the splitter ID, handling the case where it might be null
                customer.getSplitter() != null ? customer.getSplitter().getId() : null
        );
    }

    /**
     * Converts a CustomerDTO to a Customer entity.
     */
    private Customer toEntity(CustomerDTO dto) {
        Customer customer = new Customer();
        customer.setName(dto.name());
        customer.setAddress(dto.address());
        customer.setNeighborhood(dto.neighborhood());
        customer.setPlan(dto.plan());
        customer.setConnectionType(dto.connectionType());
        customer.setStatus(dto.status());
        customer.setAssignedPort(dto.assignedPort());

        // Find the associated Splitter and link it
        if (dto.splitterId() != null) {
            Splitter splitter = splitterRepository.findById(dto.splitterId())
                    .orElseThrow(() -> new EntityNotFoundException("Splitter not found with id: " + dto.splitterId()));
            customer.setSplitter(splitter);
        }

        return customer;
    }
}

